var $timerBtn={init:function(t){var e=t.time,n=$(t.container);$('<input type="button" class="timer-button" value="'+t.title+"("+e+'s)" disabled />').appendTo(n);var i=$(".timer-button"),a=window.setInterval(function(){0==--e?(window.clearInterval(a),i.removeAttr("disabled"),i.val(t.title)):i.val(t.title+"("+e+"s)")},1e3);i.click(function(){alert("SPA!10-timer-button task!")})},remove:function(){$(".main1").find(".timer-button").remove()}};