var $pwdVisual=function(){var t=$('<div class="main"><span>密码:</span><input type="password" id="password"><img src="close.jpg" id="img1" ><input type="text" id="pwd"></div>');return{init:function(){$("body").append(t);var n=t.find("#password"),i=t.find("#img1"),s=t.find("#pwd");n.on("input",function(){s.val(n.val())}),i.hover(function(){i.attr("src","open.jpg"),s.css("z-index","10")},function(){i.attr("src","close.jpg"),s.css("z-index","-10")})},get:function(){alert(t.find("#password").val())}}}();